    @aws_auth(cognito_groups: ["BESTIE", "CREATOR", "COLLAB", "ADMIN", "PRIME"])

  # Comments & likes (Fans + Besties)
  closetItemComments(
    itemId: ID!
    limit: Int
    nextToken: String
