    @aws_auth(cognito_groups: ["CREATOR", "ADMIN", "PRIME"])

  """
  Remember the last active tab in the Creator Library UI.
  Used purely for UX; backed by a lightweight user preference row.
  """
  setCreatorLibraryTab(
    input: SetCreatorLibraryTabInput!
  ): SetCreatorLibraryTabPayload!
    @aws_cognito_user_pools
    @aws_auth(cognito_groups: ["CREATOR", "ADMIN", "PRIME"])

  """
  Update the creatorâ€™s goal compass for planning + focus.
  """
  updateCreatorGoalCompass(
    input: UpdateCreatorGoalCompassInput!
  ): CreatorGoalCompass!
    @aws_cognito_user_pools
    @aws_auth(cognito_groups: ["CREATOR", "ADMIN", "PRIME"])

  # Creator filing cabinets (mutations)
  createCreatorCabinet(
    input: CreateCreatorCabinetInput!
