}

extend type Mutation {
  """
  Create a new Creator PR & Content Studio board.
  """
  createCreatorPrBoard(
    input: CreateCreatorPrBoardInput!
  ): CreatorPrBoard!
    @aws_cognito_user_pools
    @aws_auth(cognito_groups: ["CREATOR", "ADMIN", "PRIME"])

  """
  Add a content idea from Social Pulse into a PR & Content Studio board.
  """
  addContentIdeaToBoard(
    input: AddContentIdeaToBoardInput!
  ): CreatorPrItem!
    @aws_cognito_user_pools
    @aws_auth(cognito_groups: ["CREATOR", "ADMIN", "PRIME"])

  """
  Generate a fresh Creator Social Pulse (trend briefs + content ideas)
  for the current creator, backed by the Social Pulse Express Step Function.
  """
  generateSocialPulse(
    input: GenerateSocialPulseInput
  ): CreatorSocialPulse!
    @aws_cognito_user_pools
    @aws_auth(cognito_groups: ["CREATOR", "ADMIN", "PRIME"])

  saveDirectorShootPlan(input: SaveDirectorShootPlanInput!): DirectorShootPlan!

  # Run a quick emotion/content mix check vs the creator's Goal Compass
  runCreatorEmotionPreview: CreatorEmotionPreview!
}

